//Instantiate instance
$s = new OmnitureMeasurement();
//Setup application config variables
$s->account = 'myaccount';
$s->mobile = true;

//Instantiate instance
$s = OmnitureMeasurement::getInstance();
//Setup application config variables
$s->account = 'myaccount';
$s->mobile = true;

//Set Variables
$s->pageName = 'Some Page Name';
$s->channel = null;//clears any previously set value for channel
$s->prop1 = null;//clears any previously set value for prop1
$s->track();

//Set Variables
$s->pageName = 'Some Page Name';
$s->channel = 'Some Site Section';
$s->prop1 = 'Some Value';
$s->track();

//Create array of variable overrides, and pass into track call
$trackData = array('pageName' => 'Some page name',
			   	   'prop1' => 'Some prop val');
$s->track($trackData);

//Set Variables
$s->channel = 'Some Site Section';
$s->trackLink(null, 'o', 'Some Action Name');
//you can pass in null for linkURL it if doesn't apply

//Set Variables
$s->channel = null;//clears any previously set value for channel
$s->prop1 = null;//clears any previously set value for prop1
$s->trackLink('http://www.someexitdomain.com', 'e', 'Some Action Name');
