AppMeasurement & s = AppMeasurement::instance();
    s.account = "rsid";
    s.evar1 = "evar1_value";
    s.track();

s.PageName = "some page name";
//clear other variables
s.channel = "";
s.prop1 = "";
s.track();

//create a map that contains your variable overrides
map<string, string> myMap;
myMap["evar1"] = "new_evar1_value";
myMap["evar74"] = "new_evar74_value";
map<string, string> *ptr_map = &myMap;

s.trackLink("myURL", "d", "myName", ptr_map);

conbody.section:
      Plug-in Example


        
      AppMeasurement for Symbian supports a plug-in architecture that lets
		you build custom plug-ins that extend AppMeasurement for Symbian functionality.
		Use this functionality to modify the AppMeasurement for iOS object to
		manipulate data before sending it to Adobe data collection servers. 
		AppMeasurement & s = AppMeasurement::instance();
			s.delegate = this;
			s.ssl = true;
			s.usePlugins = true;
			s.account = "myreportsuiteid";
			s.pageName = "Main View";
			s.track();

// After instantiating the object, have your delegate object implement the AppMeasurementDelegate protocol

class MyClass : public AppMeasurementDelegate
{
...

};

void MyClass :: doPlugin(AppMeasurement const *s)
{
		// custom code
		s.prop4 = @"plugin";

};
